mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT EMP_NO,EMP_NAME, D.DEPT_NAME, J.JOB_NAME, (SELECT COUNT(*) FROM LATE_TB WHERE TAKEN BETWEEN TO_DATE(#{srcDateStart},'YYYY-MM-DD') AND TO_DATE(#{srcDateEnd},'YYYY-MM-DD') AND E.EMP_NO = EMP_NO) late_count, (SELECT COUNT(*) FROM LEAVE_TB WHERE TAKEN BETWEEN TO_DATE(#{srcDateStart},'YYYY-MM-DD') AND TO_DATE(#{srcDateEnd},'YYYY-MM-DD') AND E.EMP_NO = EMP_NO) leave_count, (SELECT COUNT(*) FROM ABSENT_TB WHERE TAKEN BETWEEN TO_DATE(#{srcDateStart},'YYYY-MM-DD') AND TO_DATE(#{srcDateEnd},'YYYY-MM-DD') AND E.EMP_NO = EMP_NO) absent_count FROM EMP_TB E LEFT JOIN DEPT_TB D USING (DEPT_CD) LEFT JOIN JOB_TB J USING (JOB_CD)
WHERE ${searchBy} IN #{keyword} ORDER BY DEPT_CD
WHERE EMP_NO = #{empNo}
) A ) WHERE RN BETWEEN #{start} AND #{end}

SELECT * FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT L.TAKEN, L.INOUT_FG, E.EMP_NAME FROM LOG_TB L LEFT JOIN EMP_TB E USING (EMP_NO) WHERE
TO_CHAR(TAKEN,'YYYY-MM-DD') BETWEEN TO_CHAR(TO_DATE(#{srcDateStart},'YYYY-MM-DD'),'YYYY-MM-DD') AND TO_CHAR(TO_DATE(#{srcDateEnd},'YYYY-MM-DD'),'YYYY-MM-DD')
TO_CHAR(TAKEN,'YYYY-MM-DD') IN TO_CHAR(TO_DATE(#{srcDateStart},'YYYY-MM-DD'),'YYYY-MM-DD')
AND ${searchBy} IN #{keyword}
ORDER BY TAKEN DESC ) A ) WHERE RN BETWEEN #{start} AND #{end}

SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
		FROM (
			SELECT *
			FROM WORK_TIMES_TB
			WHERE
				WORK_DAY BETWEEN TO_DATE(#{srcDateStart},'YYYY-MM-DD') AND TO_DATE(#{srcDateEnd},'YYYY-MM-DD')
				AND EMP_NO = #{empNo}
			ORDER BY WORK_DAY
) A
	)
WHERE RN BETWEEN #{start} AND #{end}

SELECT count(*) FROM EMP_TB E LEFT JOIN DEPT_TB D USING (DEPT_CD) LEFT JOIN JOB_TB J USING (JOB_CD)
WHERE ${searchBy} IN #{keyword}
ORDER BY DEPT_CD

SELECT * FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT EMP_NO, USER_ID, EMP_NAME, D.DEPT_NAME, J.JOB_NAME FROM EMP_TB E LEFT JOIN DEPT_TB D USING (DEPT_CD) LEFT JOIN JOB_TB J USING (JOB_CD)
WHERE ${searchBy} IN #{keyword}
ORDER BY DEPT_CD ) A ) WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
	UPDATE EMP_TB 
	SET 
		EMP_EMAIL = #{empEmail}, EMP_CONTACT = #{empContact}
		, EMP_ADDRESS = #{empAddress} , EMP_MSG = #{empMsg}
		
		, EMP_IMAGE = #{empImage}
		
		
		, AUTHORITY = #{authority}
		
		 
	WHERE
		EMP_NO = #{empNo}
